import api from './../../core/services/api';

class services{

    constructor(endPoint){
        this.endPoint = endPoint;
    }

    async findAll(){
        let data = [];
        await api.get(this.endPoint)
        .then((resp) => {
            data = resp.data;
        })
        .catch((err) => {
            console.log(err);
        });
        return data;
    }

    async findById(id){
        let data = [];
        await api.get(this.endPoint+"/"+id)
        .then((resp) => {
            data = resp.data;
        })
        .catch((err) => {
            console.log(err);
        });
        return data;
    }

    async create(params){
        let data = [];
        api.post(this.endPoint, params)
        .then((res) => {
            this.findAll();
            this.clearForm();
        }).catch((err) => {
            console.log(err);
        });
    }   

}

export default services;
