import React, {Component} from 'react'
//import CargoServices from '../../services/cargo.service';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import api from './../../core/services/api';
import Services from './services';

class Cargo extends Component{

    constructor(){
        super();
        this.state = {
            lista: [
                {id: "1", nome: "Amanda Souza"},
                {id: "2", nome: "Maria Carvalho"},
            ],
            nome: '',
        };
        this.services = new Services();
    }

    async listaDados(){
        // let data = [];
        // await api.get('cargo')
        // .then((res) => {
        //     this.setState({lista: res.data});
        // }).catch((err) => {
        //     console.log(err);
        // });
        this.setState({lista: services.findAll()});
    }

    componentDidMount(){
        this.listaDados();
    }
    componentWillMount(){
        services.setEndPoint('cargo');
    }

    salvaForm(evento){
        evento.preventDefault();
        if(this.validate()){
            let data = [];
            api.post('cargo', {nome: this.state.nome})
            .then((res) => {
                this.listaDados();
                this.clearForm();
            }).catch((err) => {
                console.log(err);
            });
        }
    }

    validate(){
        if(this.state.nome === ''){
            alert("é obrigatório um nome");
            return false;
        }
        return true;
    }

    setNome(evento){
        this.setState({
            nome: evento.target.value,
        })
    }

    clearForm(){
        this.setState({
            nome: '',
        });
    }

    render(){
        return (
            <div className="cargo">
                <buttom className="btn btn-success" onClick={this.teste}>Adicionar</buttom>
                <form onSubmit={this.salvaForm.bind(this)}>
                    <div className="form-control">
                        <input type="text" id="nome" value={this.state.nome} onChange={this.setNome.bind(this)} />
                    </div>
                    <div className="form-control">
                        <input type="submit" value="Salvar" className="btn btn-primary" />
                    </div>
                </form>
                <table class="table table-hover tabler-bordered">
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Nome</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.lista.map((item) => {
                                return (
                                    <tr>
                                        <td>{item.id}</td>
                                        <td>{item.nome}</td>
                                        <th>
                                            <buttom>
                                                {/* <FontAwesomeIcon icon={['fa', 'trash']} /> */}
                                                <i className="fa fa-trash"></i>
                                            </buttom>
                                        </th>
                                    </tr>
                                );
                            })
                        }
                    </tbody>
                </table>
            </div>
        );
    }

}
export default Cargo;
