import React from 'react';
import api from './../../core/services/api';

class services extends React.createClass{

    constructor(){
        this.endPoint = '';
    }

    setEndPoint(endPoint){
        this.endPoint = endPoint;
    }

    async findAll(){
        let data = [];
        await api.get(this.endPoint)
        .then((resp) => {
            data = resp.data;
        })
        .catch((err) => {
            console.log(err);
        });
        return data;
    }

    async findById(id){
        let data = [];
        await api.get(this.endPoint+"/"+id)
        .then((resp) => {
            data = resp.data;
        })
        .catch((err) => {
            console.log(err);
        });
        return data;
    }

}

export default services;
